module.exports = {
  ios: "iOS",
  android: "Android"
};

module.exports = {
  POP: "POP",
  PUSH: "PUSH",
  REPLACE: "REPLACE"
};

"use strict";
const createMemoryHistory = require("./history");
const { matchUrl } = require("./matchPath");
let rootHistory;

/**
 * Creates a new HistoryController instance
 *
 * @param {{?initialEntries: Array, ?initialIndex: number, ?keyLength: number, ?getUserConfirmation: function, sensitive: boolean, strict: boolean}} param0
 * @return {HistoryController}
 */
function createHistory({
  initialEntries = null,
  initialIndex = null,
  keyLength = null,
  getUserConfirmation = null,
  exact = false,
  sensitive = true,
  strict = false,
  path
} = {}) {
  let routeBlocker = (blockerFn, callback) => {
    // console.log(`--- routeBlocker ${blockerFn} ${_preventDefault}`);
    _preventDefault === false && getUserConfirmation
      ? getUserConfirmation(blockerFn, callback)
      : callback(true);
  };

  let _preventDefault;
  const _options = {
    exact,
    sensitive,
    strict,
    path
  };
  /**
   * @type {History}
   */
  let _history = createMemoryHistory({
    initialEntries: initialEntries || [], // The initial URLs in the history stack
    initialIndex: initialIndex || 0, // The starting index in the history stack
    keyLength: keyLength || 20, // The length of location.key
    // A function to use to confirm navigation with the user. Required
    // if you return string prompts from transition hooks (see below)
    getUserConfirmation: routeBlocker
  });

  const _listeners = new Set();
  const _unlistenAll = new Set();
  const _nodes = new Set();
  let _prompt = null;
  let _unblock;

  function listener(location, action) {
    _preventDefault === false &&
      _listeners.forEach(handler => handler(location, action));
    _preventDefault = false;
  }

  /**
   * History wrapper
   * @access package
   * @class HistoryController
   */
  class HistoryController {
    constructor() {}

    clearBlocker() {
      _unblock && _unblock();
    }

    /**
     * Prevent history change event
     */
    preventDefault() {
      _preventDefault = true;
    }

    clearPreventDefault() {
      _preventDefault = false;
    }

    block(prompt) {
      _prompt = prompt;
      this.clearBlocker();
      _unblock = _history.block(_prompt);

      return () => {
        _prompt = null;
        _unblock();
      };
    }

    clear() {
      _history.clear();
    }

    getHistoryasArray() {
      return _history.entries.map(item => item.url);
    }

    /**
     * @param {object} [={}] props Node properties
     * @return {HistoryController}
     */
    createNode(props = {}) {
      const node = createHistory(props, this);
      _nodes.add(node);
      // bubbles history goback to root if go back could be possible.
      node.onGoBack = () => {
        // console.log(`on go back ${JSON.stringify(this.getHistoryasArray())} ${_history.index}`);
        if (_history.canGo(-1)) {
          // _listeners.forEach(listener => listener(_history.location, 'POP'))
          _history.go(-1);
        } else {
          this.onGoBack && this.onGoBack();
        }
      };
      // bubbles the last push up to root until pushing can be possible.
      node.onPush = (path, data) => {
        if (this.canPush(path)) {
          this.push(path, data);
        } else {
          this.onPush && this.onPush(path, data);
        }
      };

      return node;
    }

    get lastLocation() {
      return _history.location;
    }

    /**
     * Return all nodes
     * @return {Set<HistoryController>}
     */
    get nodes() {
      return new Set(_nodes);
    }

    /**
     * @return {History}
     */
    get history() {
      return _history;
    }

    pushLocation(location) {
      _history.push(location);
    }

    /**
     * Removes last history entry
     *
     * @todo notify all listeners as action is rollback
     */
    rollback() {
      _preventDefault = true;
      _history.rollback();
      _preventDefault = false;
    }

    /**
     * Disposes history
     */
    dispose() {
      _history = null;
    }

    /**
     * If the url is available to push or not.
     *
     * @param {string} url - Url will be pushed.
     */
    canPush(url) {
      const res = matchUrl(url, _options);
      return res !== null;
    }

    /**
     * Pushes a new url to history
     *
     * @param {string} url - Url will be pushed.
     * @param {object} routeData - Requested route data
     */
    push(url, routeData = {}) {
      this.canPush(url)
        ? _history.push(url, routeData)
        : !_preventDefault &&
          this.onPush &&
          this.onPush(url, routeData, _prompt);
      _preventDefault && this.clearPreventDefault();
    }

    /**
     * If history can be gone back or not
     * @param {number} n it's a negative number to go bac
     *
     * @return boolean
     */
    canGoBack(n=-1) {
      return _history.canGo(n);
    }

    goBackto(n){
      _history.go(n) 
      _preventDefault && this.clearPreventDefault();
    }

    currentIndex(){
      return _history.index;
    }

    /**
     * Calls History.goBack
     */
    goBack() {
      _history.canGo(-1)
        ? _history.goBack()
        : !_preventDefault && this.onGoBack && this.onGoBack();
      _preventDefault && this.clearPreventDefault();
    }

    getLength(){
      return _history.length
    }

    /**
     * Adds history change handler and returns unlisten function
     *
     * @param {HistoryListener} fn Event handler callback
     * @return {function} unlisten function
     */
    listen(fn) {
      const unlisten = new Set();

      _listeners.add(fn);
      const wrapper = (location, action) => {
        !_preventDefault &&
          fn(location, action, _history.entries[_history.index]);
      };
      unlisten.add(_history.listen(wrapper));
      unlisten.forEach(item => _unlistenAll.add(item));
      return () => {
        unlisten.forEach(item => {
          item();
          _unlistenAll.delete(item);
        });
        _listeners.delete(fn);
      };
    }

    findIndex(fn){
      return _history.entries.findIndex(fn);
    }

    find(fn){
      return _history.entries.find(fn);
    }

    /**
     * Returns string representation of an instance
     *
     * @return {string}
     */
    toString() {
      return `[Object HistoryController, 
        stack: ${JSON.stringify(this.getHistoryasArray())}, 
        length ${_history.length}, 
        index : ${_history.index}]`;
    }

    /**
     * Disposes a instance
     */
    dispose() {
      _nodes.forEach(node => node.dispose());
      _unlistenAll.forEach(item => item());
      _unlistenAll.clear();
      _listeners.clear();
      _history = null;
      this.onGoBack = null;
    }
  }

  return new HistoryController();
}

module.exports = createHistory;

// https://github.com/ReactTraining/history

const warning = require("./warning");

/**
 * @ignore
 */
const createTransitionManager = () => {
  let prompt = null;

  const setPrompt = nextPrompt => {
    warning(prompt == null, "A history supports only one prompt at a time");

    prompt = nextPrompt;

    return () => {
      if (prompt === nextPrompt) prompt = null;
    };
  };

  const confirmTransitionTo = (
    location,
    action,
    getUserConfirmation,
    callback
  ) => {
    // TODO: If another transition starts while we're still confirming
    // the previous one, we may end up in a weird state. Figure out the
    // best way to handle this.
    if (prompt != null) {
      const result =
        typeof prompt === "function" ? prompt(location, action) : prompt;

      // if (typeof result === "string") {
      if (typeof getUserConfirmation === "function") {
        getUserConfirmation(result, callback);
      } else {
        warning(
          false,
          "A history needs a getUserConfirmation function in order to use a prompt message"
        );

        callback(true);
      }
      // } else {
      //   // Return false from a transition hook to cancel the transition.
      //   callback(result !== false);
      // }
    } else {
      callback(true);
    }
  };

  let listeners = [];

  const appendListener = fn => {
    let isActive = true;

    const listener = (...args) => {
      if (isActive) fn(...args);
    };

    listeners.push(listener);

    return () => {
      isActive = false;
      listeners = listeners.filter(item => item !== listener);
    };
  };

  const notifyListeners = (...args) => {
    listeners.forEach(listener => listener(...args));
  };

  return {
    setPrompt,
    confirmTransitionTo,
    appendListener,
    notifyListeners
  };
};

module.exports = createTransitionManager;

// https://github.com/ReactTraining/history
"use strict";

const resolvePathname = require("resolve-pathname/umd/resolve-pathname");
const parseUrl = require("./parseUrl");

const warning = require("./warning");
const createPath = location => {
  const { url, search, hash } = location;

  let path = url || "/";

  if (search && search !== "?")
    path += search.charAt(0) === "?" ? search : `?${search}`;

  if (hash && hash !== "#") path += hash.charAt(0) === "#" ? hash : `#${hash}`;

  return path;
};

const createTransitionManager = require("./createTransitionManager");

const clamp = (n, lowerBound, upperBound) =>
  Math.min(Math.max(n, lowerBound), upperBound);

const createLocation = (path, state, key, currentLocation) => {
  let location;
  if (typeof path === "string") {
    // Two-arg form: push(path, state)
    location = parseUrl(path);
    location.state = state;
  } else {
    // One-arg form: push(location)
    location = Object.assign({}, path);

    if (location.url === undefined) location.url = "";

    if (location.query) {
      if (location.query.charAt(0) !== "?")
        location.query = "?" + location.query;
    } else {
      location.query = "";
    }

    if (location.hash) {
      if (location.hash.charAt(0) !== "#") location.hash = "#" + location.hash;
    } else {
      location.hash = "";
    }

    if (state !== undefined && location.state === undefined)
      location.state = state;
  }

  try {
    location.url = decodeURI(location.url);
  } catch (e) {
    if (e instanceof URIError) {
      throw new URIError(
        'Pathname "' +
          location.url +
          '" could not be decoded. ' +
          "This is likely caused by an invalid percent-encoding."
      );
    } else {
      throw e;
    }
  }

  if (key) location.key = key;

  if (currentLocation) {
    // Resolve incomplete/relative pathname relative to current location.
    if (!location.url) {
      location.url = currentLocation.url;
    } else if (location.url.charAt(0) !== "/") {
      location.url = resolvePathname(
        location.url,
        currentLocation.url
      );
    }
  } else {
    // When there is no prior location and pathname is empty, set it to /
    if (!location.url) {
      location.url = "/";
    }
  }

  return location;
};

/**
 * Creates a history object that stores locations in memory.
 * @ignore
 * @return {History}
 */
const createMemoryHistory = (props = {}) => {
  const {
    getUserConfirmation,
    initialEntries = [],
    initialIndex = 0,
    keyLength = 6
  } = props;

  const transitionManager = createTransitionManager();

  const setState = nextState => {
    Object.assign(history, nextState);

    history.length = history.entries.length;

    transitionManager.notifyListeners(history.location, history.action);
  };

  const createKey = () =>
    Math.random()
      .toString(36)
      .substr(2, keyLength);

  const index = clamp(initialIndex, 0, initialEntries.length - 1);
  const entries = initialEntries.map(
    entry =>
      typeof entry === "string"
        ? createLocation(entry, undefined, createKey())
        : createLocation(entry, undefined, entry.key || createKey())
  );

  // Public interface

  const createHref = createPath;

  const confirmTransitionTo = (
    path,
    action,
    state,
    getUserConfirmation,
    handler,
    key = ""
  ) => {
    const location = createLocation(
      path,
      state,
      key || createKey(),
      history.location
    );
    transitionManager.confirmTransitionTo(
      location,
      action,
      getUserConfirmation,
      ok => handler(location, ok)
    );
  };
  let lastPath;
  const push = (path, state) => {
    lastPath = path;
    warning(
      !(
        typeof path === "object" &&
        path.state !== undefined &&
        state !== undefined
      ),
      "You should avoid providing a 2nd state argument to push when the 1st " +
        "argument is a location-like object that already has state; it is ignored"
    );

    const action = "PUSH";
    const location = createLocation(path, state, createKey(), history.location);

    transitionManager.confirmTransitionTo(
      location,
      action,
      getUserConfirmation,
      ok => {
        if (!ok) return;

        const prevIndex = history.index;
        const nextIndex = prevIndex + 1;

        const nextEntries = history.entries.slice(0);
        if (nextEntries.length > nextIndex) {
          nextEntries.splice(
            nextIndex,
            nextEntries.length - nextIndex,
            location
          );
        } else {
          nextEntries.push(location);
        }

        setState({
          action,
          location,
          index: nextIndex,
          entries: nextEntries
        });
      }
    );
  };

  const replace = (path, state) => {
    warning(
      !(
        typeof path === "object" &&
        path.state !== undefined &&
        state !== undefined
      ),
      "You should avoid providing a 2nd state argument to replace when the 1st " +
        "argument is a location-like object that already has state; it is ignored"
    );

    const action = "REPLACE";
    const location = createLocation(path, state, createKey(), history.location);

    transitionManager.confirmTransitionTo(
      location,
      action,
      getUserConfirmation,
      ok => {
        if (!ok) return;

        history.entries[history.index] = location;

        setState({ action, location });
      }
    );
  };

  const go = n => {
    const nextIndex = clamp(history.index + n, 0, history.entries.length - 1);

    const action = "POP";
    const location = history.entries[nextIndex];

    transitionManager.confirmTransitionTo(
      location,
      action,
      getUserConfirmation,
      ok => {
        if (ok) {
          setState({
            action,
            location,
            index: nextIndex
          });
        } else {
          // Mimic the behavior of DOM histories by
          // causing a render after a cancelled POP.
          setState();
        }
      }
    );
  };

  const getNextLocation = n => {
    const nextIndex = clamp(history.index + n, 0, history.entries.length - 1);
    return history.entries[nextIndex];
  };

  const goBack = () => go(-1);

  const goForward = () => go(1);
  const clear = () => {
    lastPath = null;
    history.index = -1;
    history.entries = [];
    history.length = 0;
  };

  const rollback = () => {
    history.entries.pop();
    history.length = history.length;
    history.index--;
  };

  const silencePush = (path, state) => {
    const action = "PUSH";
    const location = createLocation(path, state, createKey(), history.location);

    history.entries.push(location);
    history.length = history.length;
    history.index++;
  };

  const canGo = n => {
    const nextIndex = history.index + n;

    return nextIndex >= 0 && nextIndex < history.entries.length;
  };

  const block = (prompt = false) => transitionManager.setPrompt(prompt);

  const listen = listener => transitionManager.appendListener(listener);

  /**
   * @type History
   */
  const history = {
    length: entries.length,
    action: "POP",
    location: entries[index],
    index,
    entries,
    createHref,
    push,
    silencePush,
    replace,
    rollback,
    go,
    clear,
    goBack,
    goForward,
    canGo,
    block,
    listen,
    dispatchLast: () =>
      transitionManager.notifyListeners(history.location, history.action),
    confirmTransitionTo
  };

  return history;
};

module.exports = createMemoryHistory;

module.exports = {
  HistoryActions: require("./HistoryActions"),
  matchPatch: require("./matchPatch"),
  DeviceOS: require("./DeviceOS"),
  matchRoutes: require("./matchRoutes"),
  history: require("./history")
};

/* from react-router */

const pathToRegexp = require("path-to-regexp");
const parseUrl = require("./parseUrl");
const patternCache = {};
const cacheLimit = 10000;
let cacheCount = 0;

/**
 * @ignore
 * @param {*} pattern
 * @param {*} options
 */
const compilePath = (pattern, options) => {
  const cacheKey = `${options.end}${options.strict}${options.sensitive}`;
  const cache = patternCache[cacheKey] || (patternCache[cacheKey] = {});

  if (cache[pattern]) return cache[pattern];

  const keys = [];
  const re = pathToRegexp(pattern, keys, options);
  const compiledPattern = {
    re,
    keys
  };

  if (cacheCount < cacheLimit) {
    cache[pattern] = compiledPattern;
    cacheCount++;
  }

  return compiledPattern;
};

/**
 * Public API for matching a URL pathname to a path pattern.
 * @ignore
 */
const matchPath = (pathname, options = {}, parent) => {
  if (typeof options === "string")
    options = {
      path: options
    };

  const { path, exact = false, strict = false, sensitive = false } = options;

  if (path == null) return parent;

  const { re, keys } = compilePath(path, {
    end: exact,
    strict,
    sensitive
  });
  const match = re.exec(pathname);

  if (!match) return null;

  const url = match.shift();
  const values = match.slice();
  // [url, ...values] = ;
  const isExact = pathname === url;

  if (exact && !isExact) return null;

  return {
    path, // the path pattern used to match
    url: path === "/" && url === "" ? "/" : url, // the matched portion of the URL
    isExact, // whether or not we matched exactly
    params: keys.reduce((memo, key, index) => {
      memo[key.name] = values[index];
      return memo;
    }, {})
  };
};

/**
 * @ignore
 * @param {*} url
 * @param {*} options
 * @return {RouteMatch}
 */
const matchUrl = (url, options) => {
  const res = matchPath(parseUrl(url).url, options);
  return res;
};

module.exports = {
  compilePath,
  matchPath,
  matchUrl
};

// based from react-router
"use strict";

/**
 *
 * @ignore
 * @param {object} store
 * @param {Array<Route>} routes
 * @param {string} pathname
 * @param {Array} [branch=[]] not public API
 * @return {Array<{match: RouteMatch, route: Route}>}
 */
const matchRoutes = (store, routes, pathname, branch = []) => {
  routes.some(route => {
    const match = route.hasPath()
      ? route.matchPath(pathname)
      : branch.length
        ? branch[branch.length - 1].match
        : {
            // ensure we're using the exact code for default root match
            path: "/",
            url: "/",
            params: {},
            isExact: pathname === "/"
          };

    if (match) {
      if (route.__is_router) {
        branch.push({
          route,
          match
        });
      } else {
        !store.hasRoute(match.url) &&
          store.saveRoute(match.url, route.clone({ url: match.url }));
        branch.push({
          route: store.findRoute(match.url),
          match
        });
      }

      const children = route.map(child => {
        return child;
      });

      matchRoutes(store, children, pathname, branch);
    }

    return match;
  });

  return branch;
};

module.exports = matchRoutes;

const parseUrl = path => {
  let url = path || "/";
  let query = "";
  let rawQuery = "";
  let hash = "";

  const hashIndex = url.indexOf("#");
  if (hashIndex !== -1) {
    hash = url.substr(hashIndex);
    url = url.substr(0, hashIndex);
  }

  const queryIndex = url.indexOf("?");
  if (queryIndex !== -1) {
    rawQuery = url.substr(queryIndex);
    query =
      "{" +
      rawQuery
        .replace("?", '"')
        .replace(/\&/gi, '","')
        .replace(/\=/gi, '":"') +
      '"}';
    query = JSON.parse(query);
    url = url.substr(0, queryIndex);
  }

  return {
    query,
    url,
    rawQuery: rawQuery === "?" ? "" : rawQuery,
    hash: hash === "#" ? "" : hash
  };
};

module.exports = parseUrl;

module.exports = function(condition, message) {
  !condition && console.log(message);
};

const Renderer = require("./Renderer");
const Application = require("sf-core/application");

/**
 * Rendering strategy for Android
 * It encapsulates all logic to display pages on Android
 *
 * @class
 * @access package
 * @extends {Renderer}
 */
class AndroidRenderer extends Renderer {
  /**
   * @constructor
   */
  constructor() {
    super();
  }

  /**
   * @override
   */
  setChildControllers(controllers) {
    this._rootController.childControllers = controllers;
  }

  /**
   * @override
   */
  pushChild(page, animated = true) {
    // To avoid Android error
    if (this._rootController.childControllers.length !== 0 && this._rootController.childControllers.some(p => p === page)) {
      return;
    }
    animated = this._rootController.childControllers.length === 0 ? false : animated;
    this._rootController.push &&
      this._rootController.push({ controller: page, animated: animated });
    this._activePage = page;
  }

  /**
   * @override
   */
  onNavigationControllerTransition(fn) {
    if (this._rootController.onTransition) {
      this._rootController.onTransition = fn;
      return () => (this._rootController.onTransition = () => null);
    }

    return () => null;
  }

  /**
   * @override
   */
  popChild(animated = true) {
    this._rootController.pop &&
      this._rootController.pop({ animated: animated });
  }

  /**
   * @override
   */
  show(page) {
    if(this._activePage == page)
      return;
    Application.setRootController(page);
    this._activePage = page;
  }
}

module.exports = AndroidRenderer;

"use strict";

/**
 * @typedef {object<string,string|object>} BottomTabBarItem Represent {@link TabBarItem} params
 * @property {Image} icon
 * @property {string} title
 */

/**
 * @typedef {RouterParams} BottomTabBarRouterParams
 * @property {Array<BottomTabBarItem>} items BottomTabBarItem collection
 * @property {object} tabbarParams See {@link BottomTabbarController}
 */

const NativeRouterBase = require("./NativeRouterBase");
const BottomTabBarController = require("sf-core/ui/bottomtabbarcontroller");
const createRenderer = require("./createRenderer");
const TabBarItem = require("sf-core/ui/tabbaritem");
const functionMaybe = require("../utils/funcorVal");

/**
 * @private
 * @param {TabBarItem} item
 */
function createTabBarItem(item) {
  return item instanceof TabBarItem ? item : new TabBarItem(item);
}

const userTabStatus = {
  IDLE: 0,
  WAITING: 1,
  DONE: 2
};

/**
 * It creates {@link BottomTabbarController} and manages its behavours and routes.
 *
 * @class
 * @extends {Router}
 * @example
 * const {BottommTabBarRouter, Route} = require('@smartface/router')
 * const Image = require('sf-core/ui/image');
 * const Color = require('sf-core/ui/color');
 *
 * var router = Router.of({
 *  path: "/",
 *  routes: [
 *    BottomTabBarRouter.of(
 *      path: '/tabs',
 *      tabbarParams: () => ({
 *        itemColor: {normal: Color.BLACK, selected: Color.BLUE},
 *        backgroundColor: Color.BLUE,
 *      }),
 *      tabbarItems: [
 *        { title: "Page1", icon: Image.createFromFile("images://icon1.png") },
 *        { title: "Page2", icon: Image.createFromFile("images://icon2.png") },
 *      ],
 *      routes: [
 *        Route.of({
 *          routeDidEnter: (router, route) => {
 *          },
 *          routeDidExit: (router, route) => {
 *          },
 *          path: "/tabs/page1",
 *          build((router, route) => {
 *            const Page1 = require('/pages/Page1');
 *            return new Page1(route.getState().routeData, router);
 *          })
 *        }),
 *        Route.of({
 *          path: "/tabs/page2",
 *          build((router, route) => {
 *            const Page2 = require('/pages/Page2');
 *            return new Page2(route.getState().routeData, router);
 *          });
 *        });
 *      ]
 *    )]
 * })
 *
 * @since 1.0.0
 */
class BottomTabBarRouter extends NativeRouterBase {
  /**
   * Builds OS specific NaitveRouter
   *
   * @static
   * @param {BottomTabBarRouterParams} params
   */
  static of(params) {
    params.renderer = createRenderer();
    return new BottomTabBarRouter(params);
  }

  /**
   * @constructor
   * @param {BottomTabBarRouterParams} param
   */
  constructor({
    path = "",
    routes = [],
    exact = false,
    renderer = null,
    to = null,
    tabbarParams = {},
    items = [],
    isRoot = false,
    routerDidEnter,
    routerDidExit,
    routeShouldMatch,
    routeWillEnter
  }) {
    super({
      path,
      routes,
      exact,
      to,
      isRoot,
      routerDidEnter,
      routerDidExit,
      routeShouldMatch,
      routeWillEnter
    });

    this._renderer = renderer;
    this._renderer.setRootController(new BottomTabBarController(tabbarParams));
    this._visitedIndexes = { length: 0 };
    this._items = items;
    this._tabStatus = userTabStatus.IDLE;

    this._renderer._rootController.shouldSelectByIndex = ({ index }) => {
      // TabbarItem should be changed
      return this.shouldSelectByIndex(index);
    };

    this._renderer._rootController.didSelectByIndex = ({ index }) => {
      this.pushRoute(this._routes[index]);
      this._currentIndex = index;
    };

    // Initilaze BottomTabBarController's TabBarItems
    this._renderer._rootController.tabBar = tabbarParams();
  }

  initialize(parentHistory, onHistoryChange, pushHomes) {
    super.initialize(parentHistory, onHistoryChange, pushHomes);
    // Initilaze BottomTabBarController's child controllers
    this._renderer.setChildControllers(
      this._routes.map(route => route.build(this, route))
    );

    this._renderer.setTabBarItems(
      functionMaybe(this._items).map(createTabBarItem)
    );
    this._renderer.showTab();
    // Overrides build method
    this.build = () => this._renderer._rootController;
  }

  /**
   * @ignore
   * @protected
   * @param {number} index
   */
  shouldSelectByIndex(index) {
    if (this._currentIndex !== index) {
      this._fromRouter = false;
    }

    /*this._fromRouter = false;
    if (
      (this._tabStatus === userTabStatus.IDLE &&
        index !== this._currentIndex) ||
      this._tabStatus === userTabStatus.WAITING
    ) {
      this._tabStatus = userTabStatus.WAITING;*/
    /*(async function(scope) {
      console.log(`${scope} ${scope._routes[index]} ${index}`);
      scope.pushRoute(scope._routes[index]);
      return scope;
    })(this)
      .then(scope => {
        scope._fromRouter = true;
      })
      .catch(e => alert(e.message + " " + e.stack, "Error"));*/

    // setTimeout(() => {
    //   this.pushRoute(this._routes[index]);
    //   this._fromRouter = true;
    // });
    // }

    return this._currentIndex !== index;
    /*return (
      this._currentIndex != index && this._tabStatus === userTabStatus.IDLE
    );*/
  }

  /**
   * Sets TabBarItems visited by TabBarItem index
   *
   * @protected
   * @param {number} index
   * @param {string} path
   */
  setVisited(index, path) {
    if (index < 0) return;
    this._visitedIndexes[index] = {
      path
    };
    this._visitedIndexes.length++;
  }

  /**
   * CHecks if TabBarItem is visited before
   *
   * @protected
   * @param {number} index
   * @return {boolean}
   */
  isVisited(index) {
    return !!this._visitedIndexes[index];
  }

  /**
   * Finds child route's index by path
   *
   * @protected
   * @param {string} path
   */
  resolveIndex(path) {
    return this._routes.findIndex(
      route => route.getUrlPath() === path || route.getRedirectto() === path
    );
  }

  /**
   * Finds child route by index
   *
   * @protected
   * @param {number} index
   */
  resolveRoute(index) {
    return this._routes.find((route, ind) => ind === index);
  }

  canGoBack(n){
    return this._historyController.canGoBack(n);
  }

  /**
   * @override
   */
  dispose() {
    super.dispose();
    this._unlistener();
    this._renderer.dispose();
    this._renderer._rootController.didSelectByIndex = () => null;
    this._items = null;
    this._renderer = null;
  }

  push(path, routeData = {}) {
    const index = this.resolveIndex(path);

    if (this._fromRouter === false) {
      if (this.isVisited(index)) {
        return super.push(this._visitedIndexes[index].path, routeData);
      }
    }

    return super.push(path, routeData);
  }

  /**
   * Before route entered
   *
   * @event
   * @protected
   */
  /*routeWillEnter(route, [url, backUrl]) {
    // const { match: next } = matches[matches.length - 1];
    const { match, url: routeUrl } = route.getState();
    console.log(`bottomtabbar ${JSON.stringify(match)} ${JSON.stringify(route.match)} ${url} back to ${backUrl}`)
    // matches[1] || matches[0];
    // const lastIndex = this.resolveIndex(next.path);
    const index = this.resolveIndex(routeUrl);
    // sets target tabbar item as visited.
    // selects target tabbaritem by index
    if (this._currentRouteUrl !== url) {
      this._renderer.setSelectedIndex(index);
      this._renderer.showTab();
      this.setVisited(index, backUrl || url);
    }
    // this.isVisited(index) && this.activateIndex(index);
    this._currentIndex = index;
    // if (userTabStatus.WAITING) this._tabStatus = userTabStatus.IDLE;
    this._currentRouteUrl = url;
  }*/

  /**
   * @override
   */
  renderMatches(matches, location, action, target, fromRouter) {
    // this._fromRouter = true;
    if (matches.length > 0) {
      const { match: next } = matches[matches.length - 1];
      const { match } = matches[1] || matches[0];
      // const lastIndex = this.resolveIndex(next.path);
      const index = this.resolveIndex(match.path);
      // sets target tabbar item as visited.
      // selects target tabbaritem by index
      this._renderer.setSelectedIndex(index);
      if(this._currentIndex != index)
        this._renderer.showTab();
      this.setVisited(index, location.url);
      this._currentIndex = index;
      if (userTabStatus.WAITING) this._tabStatus = userTabStatus.IDLE;
    }

    super.renderMatches(matches, location, action, target, fromRouter);
  }
}

module.exports = BottomTabBarRouter;

