"use strict";

const Renderer = require("./Renderer");
const Animator = require("./iOSAnimator");

/**
 * Rendering strategy for iOS
 * It encapsulates all logic to display pages on iOS
 *
 * @class
 * @access package
 * @extends {Renderer}
 */
class IOSRenderer extends Renderer {
  /**
   * @constructor
   */
  constructor() {
    super();
  }

  /**
   * @override
   */
  setChildControllers(controllers) {
    this._rootController.childControllers = controllers;
  }

  /**
   * @override
   */
  showWithTransition(fromPage, toPage, duration = 0, options = 0 << 20) {
    new Animator(this._rootController)
      .onAnimate((container, from, to, params) => {
        this.addChild(to);
      })
      .onFinish((finished, container, from, to, params) => {
        to.nativeObject.didMoveToParentViewController(container.nativeObject);
        this.removeChild(from);
      })
      .once(true)
      .start(fromPage, toPage, duration, options);
  }

  /**
   * @override
   */
  pushChild(page, animated = true) {
    // prevents to push existing view
    if (this._rootController.childControllers.some(p => p === page) || this._activePage === page) {      return;
      // throw new Error("You can't pass extisting view");
    }

    this.makeRootVisible();
    this._rootController.push &&
      this._rootController.push({ controller: page, animated });
    this._activePage = page;
  }

  /**
   * @override
   */
  onNavigationControllerTransition(fn) {
    this._rootController.onTransition = fn;
    return () => (this._rootController.onTransition = () => null);
  }

  /**
   * @override
   */
  popChild(animated = true) {
    this.makeRootVisible();
    if (this._rootController.childControllers.length > 1)
      this._rootController.pop({ animated });
  }

  /**
   * @override
   */
  removeChild(page) {
    page.nativeObject.removeFromParentViewController();
    page.nativeObject.view.removeFromSuperview();
  }

  /**
   * @override
   */
  addChild(page) {
    this.makeRootVisible();
    page.nativeObject.view &&
      this._rootController.nativeObject.view.addSubview(page.nativeObject.view);
  }

  /**
   * @override
   */
  addPageViewController(page) {
    this._rootController.nativeObject.addChildViewController(page.nativeObject);
  }

  /**
   * @override
   */
  show(page) {
    if (this._currentPage === page) return;

    if (this._currentPage) {
      // this.showWithTransition(this._currentPage, page);
      this.removeChild(this._currentPage);
    }
    // else {
    this.addPageViewController(page);
    this.addChild(page);
    page.nativeObject.didMoveToParentViewController(
      this._rootController.nativeObject
    );
    // }

    this._currentPage = page;

    this.makeRootVisible();
  }
}

module.exports = IOSRenderer;

"use strict";

const NativeRouterBase = require("./NativeRouterBase");
const createRenderer = require("./createRenderer");
const Page = require("sf-core/ui/page");

/**
 * It creates a root fragment Rotuer adds and removes child routers and pages as application root.
 *
 * @class
 * @example
 * const {NativeRouter: Router, Route} = require('@smartface/router')
 * const Image = require('sf-core/ui/image');
 * const Color = require('sf-core/ui/color');
 *
 * var router = Router.of({
 *  path: "/",
 *  isRoot: true,
 *  to: '/page1',
 *  routes: [
 *    Route.of({
 *      path: "/page1",
 *      build((router, route) => {
 *        const Page1 = require('/pages/Page1');
 *          return new Page1(route.getState().routeData, router);
 *         })
 *      }),
 *      Route.of({
 *        path: "/page2",
 *        build((router, route) => {
 *          const Page2 = require('/pages/Page2');
 *          return new Page2(route.getState().routeData, router);
 *        });
 *      });
 *    ]
 * })
 *
 * @since 1.0.0
 */
class NativeRouter extends NativeRouterBase {
  /**
   * Create OS specific NativeRouter instance
   * @static
   * @param {RouterParams} options
   * @returns {NativeRouter}
   */
  static of(options) {
    options.renderer = createRenderer();
    return new NativeRouter(options);
  }

  /**
   * @constructor
   * @param {RouterParams} param
   */
  constructor({
    path = "",
    build = null,
    routes = [],
    exact = false,
    renderer = null,
    to = null,
    routerDidEnter,
    routerDidExit,
    routeShouldMatch,
    routeWillEnter,
    rootWillChange = null,
  }) {
    super({
      path,
      build,
      routes,
      exact,
      isRoot: true,
      to,
      routeWillEnter,
      routerDidEnter,
      routerDidExit,
      routeShouldMatch
    });

    this._rootWillChange = rootWillChange;

    if (!this._isRoot) {
      throw new Error("[NativeRouter] Please only use as root");
    }

    this._renderer = renderer;
  }

  canGoBack(n){
    return false;
  }

  /**
   * @override
   */
  routeWillEnter(route, action) {
    const Renderer = require("./Renderer");
    // this._renderer.show(router._renderer._rootController);
    if (this._isRoot && this._route !== route) {
      Renderer.setasRoot(
        // If route is instance of Router
        (route._renderer && route._renderer._rootController) ||
        // else just instance of Route
          route.getState().view
      );
      this._route = route;
    }

    super.routeWillEnter(route, action);
  }
}

module.exports = NativeRouter;

