"use strict";

const Page = require("sf-core/ui/page");
const Application = require("sf-core/application");

/**
 * Abstract Renderer Strategy
 * It encapsulates displaying strategies for child views.
 *
 * @access package
 * @abstract
 */
class Renderer {
  /**
   * Helper method sets statically rootController of the Application by DeviceOS
   *
   * @static
   * @param {BottomTabBarController|Page|NavigationController} rootController
   */
  static setasRoot(rootController) {
    Application.setRootController({
      controller: rootController,
      animated: true
    });
  }

  makeRootVisible() {
    var sfWindow = SF.requireClass("UIApplication").sharedApplication()
      .keyWindow;
    sfWindow.makeKeyAndVisible();
  }

  showTab(){
    this._rootController.show();
  }

  present(controller, animated, onComplete) {
    console.log("call present");
    this._rootController.present({
      controller,
      animated,
      onComplete
    });
  }

  dismiss(onComplete) {
    this._rootController.dismiss({ onComplete });
  }

  replaceChild(view, index=null){
    if(!this._rootController.childControllers || !this._rootController.childControllers.length)
      return;

    index = index || this._rootController.childControllers.length - 1;
    const controllers = this._rootController.childControllers;
    controllers[index] = view;
    this._rootController.childControllers = controllers;
  }

  /**
   * Only use if rootpage is Page instancea
   *
   * @protected
   * @param {Page} fromPage
   * @param {Page} toPage
   * @param {number} [duration=0] duration
   * @param {number} [options=0] options
   */
  showWithTransition(fromPage, toPage, duration = 0, options = 0) {
    throw new Error("onNavigatorChange method must be overridden");
  }

  /**
   * Template method sets specified controller as root controller
   * @param {BottomTabBarController|Page|NavigationController} controller
   */
  setRootController(controller) {
    this._rootController = controller;
  }

  /**
   * NavigationController child page is changed handler
   * Only use if rootpage is NavigationController.
   * Must be Implemented
   *
   * @param {function(e:NavigationControllerTransformEvent)} fn
   */
  onNavigationControllerTransition(fn) {
    throw new Error("onNavigatorChange method must be overridden");
  }

  /**
   * Set TabBarItems to BottomTabBarController
   *
   * @param {Array<TabBarItem>} items
   */
  setTabBarItems(items) {
    this._rootController.tabBar.items = items;
  }

  /**
   * Set NavigationController selected index
   *
   * @param {nummer} index
   */
  setSelectedIndex(index) {
    if(this._rootController.selectedIndex != index)
      this._rootController.selectedIndex = index;
  }

  /**
   * Set NavigationController child controllers.
   * Must be implemented.
   *
   * @params {Array<NavigationController>} children
   */
  setChildControllers(children) {
    throw new Error("addChildViewControllers method must be overridden");
  }

  /**
   * Remove child from root page
   * Must be implemented.
   *
   * @param {Page} page
   */
  removeChild(page) {
    throw new Error("removeChild must be overridden");
  }

  /**
   * Add child from root page
   * Must be implemented.
   *
   * @param {Page} page
   */
  addChild(page) {
    throw new Error("addChild must be overridden");
  }

  /**
   * Push child from root NavigationController.
   * Must be implemented.
   *
   * @param {Page} page
   * @param {boolean} [animated=true] animmated
   */
  pushChild(page, animated = true) {
    throw new Error("pushChild must be overridden");
  }

  /**
   * Pop child from root NavigationController.
   * Must be implemented.
   *
   * @param {boolean} [animated=true] animmated
   */
  popChild(animated = true) {
    throw new Error("popChild must be overridden");
  }

  /**
   */
  popTo(n) {
    this._rootController.popTo({ controller: this._rootController.childControllers[n], animated: true });
  }

  /**
   * Displays specified page
   * Only use if root conttroller is a Page instance
   *
   * @param {Page} page
   */
  show(page) {
    throw new Error("popChild must be overridden");
  }
}

module.exports = Renderer;

const DeviceOS = require("../common/DeviceOS");

module.exports = function createRenderer(root, params = {}) {
  let Renderer;
  switch (Device.deviceOS) {
    case DeviceOS.ios:
      Renderer = require("./IOSRenderer");
      break;
    case DeviceOS.android:
      Renderer = require("./AndroidRenderer");
      break;
    default:
      throw new TypeError(Device.deviceOS + " Invalid OS definition.");
  }

  return new Renderer(root, params);
};

const TransitionOptions = require("./IOSTransitions");

/**
 * @ignore
 *
 */
class iOSAnimator {
  constructor(containerView) {
    this._containerView = containerView;
    this._options = 0 << 20;
  }

  onAnimate(fn) {
    this._onAnimate = fn;
    return this;
  }

  onFinish(fn) {
    this._onFinish = fn;
    return this;
  }

  start(from, to, duration = 0, params = {}) {
    // options && (this._options = this._options | options);
    // this.getTime();
    this._containerView.nativeObject.transitionFromToDurationOptionsAnimationsCompletion(
      from.nativeObject,
      to.nativeObject,
      duration,
      TransitionOptions.UIViewAnimationOptionTransitionNone,
      () => {
        this._onAnimate &&
          this._onAnimate(
            this._containerView,
            from,
            to,
            duration,
            this._options,
            params
          );
      },
      finished => {
        this._onFinish &&
          this._onFinish(
            finished,
            this._containerView,
            from,
            to,
            duration,
            this._options,
            params
          );
        this._once && this.dispose();
      }
    );
  }

  dispose() {
    this._containerView = null;
    this._onFinish = null;
    this._onAnimate = null;
    this._easingFn = null;
  }

  once(val) {
    this._once = val;
    return this;
  }
}

module.exports = iOSAnimator;

"use strict";

/**
 * @typedef {object} RouteMatch Object seperately keeps parsed and matched data of the request for every route
 * @property {boolean} isExact if Requested path is an exact match or not.
 * @property {Object} params Request params like id or name is represented by '/path/:id' or '/path2/:name'
 * @property {string} path Matched route's path
 * @property {string} url Requested route path
 */

/**
 * @typedef {object} RouteLocation History entry of a request
 * @property {!string} url Requested url
 * @property {?string} query Url's search data
 * @property {?string} hash Url's hash data
 * @property {?RouteState} state Requested data to destination route
 * @property {!string} key Auto generated unique key
 */

/**
 * @typedef {object} RouteParams
 * @property {!string} path Route Path definition
 * @property {?string | ?function(router: Router, route: route)} [to=null] Redirection url
 * @property {?Array<Route>} [routes=[]] Route children
 * @property {?function(router: Router, route: Route)} [build=null] Route's view builder
 * @property {?Array<Route>} [build=null] Route's view builder
 * @property {?boolean} [exact=false]  Points that a route match must be exact or not. If a route is exact then it's chld routes cannot be received.
 * @property {?boolean} [sensitive=false] If path matching is case-sensitive or not.
 * @property {?modal} [modal=false] If route is displayed as modal or not.
 * @property {?function(route: Route, nextState: RouteState)} routeShouldMatch Handles if route is mathed as request url
 * @property {?function(Router: Router, route: Route)} routeDidEnter Handles if route is displayed
 * @property {?function(Router: Router, route: Route)} routeDidExit Handles if route is removed
 */

/**
 * @typedef {object} RouteState
 * @property {?object} [routeData ={}] Requested data by user
 * @property {!string} action Request action 'PUSH', 'POP' or 'REPLACE'
 * @property {object} query Request's query-string
 * @property {string} rawQuery String version of the request's query-string
 * @property {boolean} active If Route is currently displayed or not.
 * @property {string} hash Request's url hash comes after '#' char. For example '/path/to#a-hash'
 * @property {!RouteMatch} match Request's match result
 * @property {!object} view Keeps requested route's view
 * @property {!string} url Requested url
 * @property {!string} prevUrl Previously requested url
 * @property {?object} [routingState={}] Keeps user data when route runs
 */

const matchUrl = require("../common/matchPath").matchUrl;
const mapComposer = require("../utils/map");

/**
 * Route's path ValueObject
 * For internal use
 * @access private
 * @class
 * @since 1.0.0
 */
class RoutePath {
  /**
   * Factory method to create a new instance
   *
   * @param {string} path
   * @since 1.0.0
   */
  static of(path) {
    return new RoutePath(path);
  }

  /**
   * @constructor
   * @param {string} path
   * @since 1.0.0
   */
  constructor(path) {
    this._path = path;
  }

  /**
   * Returns route path
   * @return {string}
   * @since 1.0.0
   */
  getPath() {
    return this._path;
  }

  /**
   * Returns route is root or not.
   *
   * @returns {boolean}
   * @since 1.0.0
   */
  isRoot() {
    return this._path === "/";
  }

  /**
   * Return quick representaion of the route-path
   *
   * @since 1.0.0
   * @returns {{path: string, isRoot: boolean}}
   */
  toObject() {
    return {
      path: this._path,
      isRoot: this.isRoot
    };
  }

  /**
   * @since 1.0.0
   * @returns {RoutePath}
   */
  clone() {
    return new RoutePath(this._path);
  }

  /**
   * Return path is empty or not
   *
   * @since 1.0.0
   * @return {boelean}
   */
  hasPath() {
    return this._path !== null || this._path !== undefined || this._path !== "";
  }
}

/**
 * Route implementation
 *
 * @since 1.0.0
 * @class
 */
class Route {
  /**
   * Static helper method to create a new instance of Route
   *
   * @since 1.0.0
   * @static
   * @param {RouteParams} params Route properties
   * @param {RouteState} state Initial state
   * @return {Route}
   */
  static of(params = {}, state = {}) {
    return new Route(params, state);
  }
  /**
   * @constructor
   * @param {RouteParams} param0 Route properties
   * @param {RouteState} param1 Initial state
   */
  constructor(
    {
      path = null,
      to = null,
      routes = [],
      build = null,
      exact = false,
      sensitive = false,
      strict = false,
      modal = false,
      routeShouldMatch = null,
      routeDidEnter,
      routeDidExit
    } = {},
    {
      match = {},
      routeData = {},
      view = null,
      routingState = {},
      action = null,
      url = null,
      active = false,
      query = "",
      rawQuery = null,
      hash = ""
    } = {}
  ) {
    this._options = {
      exact,
      path,
      sensitive,
      strict
    };
    this._exact = exact;
    this._isDIrty = false;
    this._strict = false;
    this._build = build;
    this._path = path instanceof RoutePath ? path : new RoutePath(path);
    this._routes = routes;
    this.map = mapComposer.call(this, this._routes);
    this._to = to;
    this._routeShouldMatch = routeShouldMatch;
    this._routeDidEnter = routeDidEnter;
    this._routeDidExit = routeDidExit;
    this._modal = modal;
    this._state = Object.seal({
      match,
      query,
      rawQuery,
      hash,
      routeData,
      view,
      routingState,
      action,
      url,
      active,
      prevUrl: null
    });
  }

  /**
   *
   */
  isModal() {
    return this._modal;
  }

  /**
   * Merges specified state to current route state
   *
   * @since 1.0.0
   * @param {object} state
   */
  setState(state) {
    this._state = Object.assign(this._state, state);
  }

  /**
   * Returns Route's current state
   *
   * @since 1.0.0
   * @return {RouteState}
   */
  getState() {
    return this._state;
  }

  /**
   * Simple Object representation of the route
   *
   * @since 1.0.0
   * @return {{path: string, routes: Array<object>}}
   */
  toJSON() {
    // const {
    //   match,
    //   routeData,
    //   routingState,
    //   action,
    //   url,
    //   view,
    //   active,
    //   prevUrl
    // } = this._state;
    return {
      type: "route",
      match: this._state.routeData,
      routeData: this._state.routeData,
      routingState: this._state.routingState,
      path: this._path.getPath(),
      routes: this._routes.slice(),
      state: Object.assign({}, this._state, {
        view:
          (this._state.view && this._state.view.constructor.name) || undefined
      })
    };
  }

  /**
   * String representation of the route
   *
   * @since 1.0.0
   * @return {string}
   */
  toString() {
    return `[object ${
      this.constructor.name
    }, path: ${this.getUrlPath()}, url: ${this._state.url}]`;
  }

  setUrl(url) {
    if (!url) throw new TypeError(`[${this}] Route url cannot be empty`);
    this.setState({ url, prevUrl: this._state.url });
    this._isDIrty = true;
  }

  clearUrl() {
    this.setState({ url: "", prevUrl: this._state.url });
  }

  clearDirty() {
    this._isDIrty = false;
  }

  getUrl() {
    return this._state.url;
  }

  /**
   * Helper method to return excat path of the component
   *
   * @since 1.0.0
   * @return {string}
   */
  get routePath() {
    return this.getRedirectto() || this.getUrlPath();
  }

  /**
   * Returns redirection path
   *
   * @since 1.0.0
   * @return {string}
   */
  getRedirectto() {
    return this._to;
  }

  /**
   * Builds a route's view.
   * This method is called whenever router is routing into that path.
   * There are some exceptions:
   * - going into a tab, which the tab is created before
   * - for iOS, goingBack via gesture or headerBar back
   *
   * @since 1.0.0
   * @param {Router} router - Not the root router, the router which the route belongs to.
   * @return {Page} view = null - If the route has been built once, the previous view (page) is given. Otherwise it is null. If view is not null, returning the view back makes it singleton.
   */
  build(router) {
    return this._build ? this._build(router, this) : null;
  }

  /**
   * Triggered before when an exact match happends.
   * If the routeShouldMatch eventhandler is set
   * and routeShouldMatch returns 'true' then match happends
   * or routeShouldMatch returns 'false' then match is blocked
   * @example
   * ....
   * Route.of({
   *  routeShouldMatch: (router, route) => {
   *    return true;
   *  }
   * })
   *
   * ...
   *
   * @protected
   * @since 1.0.0
   * @event
   * @emits routeShouldMatch(router: Router, route: Route)
   * @param {Router} router
   * @return {boolean}
   */
  routeShouldMatch(router) {
    return this._routeShouldMatch ? this._routeShouldMatch(router, this) : true;
  }

  /**
   * Handles route is matched and displayed
   *
   * @example
   * ....
   * Route.of({
   *  routeDidEnter: (router, route) => {
   *    ...
   *  }
   * })
   *
   * ...
   * @emits routeDidEnter
   * @event
   * @param {Router} router
   */
  routeDidEnter(router) {
    return this._routeDidEnter ? this._routeDidEnter(router, this) : true;
  }

  /**
   * Handles that route is removed by router
   * @example
   * ....
   * Route.of({
   *  routeDidExit: (router, route) => {
   *    ...
   *  }
   * })
   *
   * ...
   * @since 1.0.0
   * @emits routeDidExit
   * @event
   * @param {Router} router
   */
  routeDidExit(router) {
    return this._routeDidExit ? this._routeDidExit(router, this) : true;
  }

  /**
   * If Route has a path or not
   *
   * @since 1.0.0
   * @returns {boolean}
   */
  hasPath() {
    return this._path.hasPath();
  }

  /**
   * Checks if the specified url match to the route path
   * @param {string} url
   * @return {RouteMatch}
   */
  matchPath(url) {
    return matchUrl(url, this._options);
  }

  /**
   * Returns route path as string
   *
   * @since 1.0.0
   * @returns {string}
   */
  getUrlPath() {
    return this._path.getPath();
  }

  /**
   * Clones route's path and returns
   *
   * @since 1.0.0
   * @return {RoutePath}
   */
  getPath() {
    return this._path.clone();
  }

  /**
   * Clones new instance of the route
   * @since 1.0.0
   * @ignore
   * @returns {Route}
   */
  clone(state = {}) {
    return Route.of(
      {
        to: this._to,
        modal: this._modal,
        routeDidExit: this._routeDidExit,
        routeShouldMatch: this._routeShouldMatch,
        routeDidEnter: this._routeDidEnter,
        exact: this._exact,
        strict: this._strict,
        path: this._path.clone(),
        path: this._path,
        props: Object.assign({}, this._props),
        build: this._build
      },
      Object.assign(
        {
          hash: this._state.hash,
          query: this._state.query,
          rawQuery: this._state.rawQuery,
          action: this._state.action,
          active: this._state.active,
          url: this._state.url,
          view: this._state.view,
          query: this._state.query
        },
        state
      )
    );
  }
}

module.exports = Route;

"use strict";

const Route = require("./Route");
const matchRoutes = require("../common/matchRoutes");
const createHistory = require("../common/createHistory");
const createStore = require("./routeStore");
const funcorVal = require("../utils/funcorVal");
let tasks = [];

let historyController;

let _lastRoute;
const listeners = new Set();
const history = [];
let store;

const dispatch = (location, action) => {
  history.push([location.pathnamme, action]);
  listeners.forEach(listener => listener(location, action));
  action === "PUSH"
    ? historyController.pushLocation(location) // TODO: not share loaction instance
    : historyController.goBack();
};

function handleRouteUrl(router, url, routeData, action) {
  if (url === router._state.prevUrl) return;

  router._historyController.preventDefault();
  switch (action) {
    case "PUSH":
      router._historyController.push(url, routeData);
      break;
    case "POP":
      router._historyController.goBack();
      break;
  }
}

/**
 * @typedef {object} RouterParams
 * @property {!string} [path]  Routing path
 * @property {!Array<Route>} [routes=[]] Child routes
 * @property {?boolean} [exact=false] If it's only exact match or not
 * @property {?boolean} [isRoot=false] If it's root router
 * @property {?(string|null)} to Redirection path
 * @property {?boolean} [strict=false] strict
 * @property {?boolean} [sensitive=false] sensitive Path is case sensitive or not
 * @property {?function(router: Router, prevUrl: string, currentUrl: stirng, action: string)} routerDidEnter Handles the Router is actived.
 * @property {?function(router: Router, prevUrl: string, action: string)} routerDidExit Handles the Router is deactived.
 * @property {?function(router: Router, route: Route, viewController : NavigationController)} [routeWillEnter=null] A route or a router which is called by request in the router will be entered.
 * @property {number} homeRoute Home route index of the router's children. If it pushes first when routed to router's.
 */

/**
 * Router Core Implemantation
 *
 * @description
 * Router implementation creates a thin layer between view and application layers
 * to handle view changes and to create loosely coupled and resuable routing logic.
 *
 * @class
 *
 * @example
 *  const router = Router.of({
 *   path: "/",
 *   to: "/pages/page2",
 *   isRoot: true,
 *   routes: [
 *       Route.of({
 *           path: "/pages/page2",
 *           build: (match, state) => {
 *               let Page2 = require("pages/page2");
 *               return new Page2();
 *           }
 *       }),
 *       StackRouter.of({
 *           path: "/stack",
 *           to: "/stack/path1",
 *           headerBarParams: () => { ios: { translucent: true } },
 *           routes: [
 *               Route.of({
 *                   path: "/stack/path1",
 *                   build: (match, state, router) => new Page1(state.data, router)
 *               }),
 *               Route.of({
 *                   path: "/stack/path2",
 *                   routeShouldMatch: (route, nextState) => {
 *                       if (!nextState.routeData.applied) {
 *                           // blocks route changing
 *                           return false;
 *                       }
 *                       return false;
 *                   },
 *                   build: (router, route) => {
 *                       const { routeData, view } = route.getState();
 *                       return new Page2(routeData, router);
 *                   }
 *               })
 *           ]
 *       }),
 *       BottomTabBarRouter.of({
 *           path: "/bottom",
 *           to: "/bottom/stack2/path1",
 *           tabbarParams: () => ({
 *               ios: { translucent: false },
 *               itemColor: Color.RED,
 *               unselectedItemColor: Color.YELLOW,
 *               backgroundColor: Color.BLUE
 *           }),
 *           items: () => [{ title: "page1" }, { title: "page2" }, { title: "page3" }],
 *           routes: [
 *               StackRouter.of({
 *                   path: "/bottom/stack",
 *                   to: "/bottom/stack/path1",
 *                   headerBarParams: () => { ios: { translucent: false } },
 *                   routes: [
 *                       Route.of({
 *                           path: "/bottom/stack/path1",
 *                           build: (router, route) => new Page1(route.getState().routeData, router, "/stack/path2")
 *                       }),
 *                       Route.of({
 *                           path: "/bottom/stack/path2",
 *                           build: (router, route) => {
 *                               const { routeData, view } = route.getState();
 *
 *                               return new Page2(routeData, router, "/bottom/stack2/path1");
 *                           }
 *                       })
 *                   ]
 *               }),
 *               StackRouter.of({
 *                   path: "/bottom/stack2",
 *                   to: "/bottom/stack2/path1",
 *                   headerBarParams: () => { ios: { translucent: false } },
 *                   routes: [
 *                       Route.of({
 *                           path: "/bottom/stack2/path1",
 *                           build: (router, route) => new Page1(route.getState().routeData, router, "/bottom/stack/path2")
 *                       }),
 *                       Route.of({
 *                           path: "/bottom/stack2/path2",
 *                           build: (router, route) => {
 *                               return new Page2(route.getState().routeData, router);
 *                           }
 *                       })
 *                   ]
 *               })
 *           ]
 *       })
 *   ]
 * });
 *
 * @example
 * // Homeroute setting
 *
 * ...
 *      StackRouter.of({
 *           path: "/stack",
 *           to: "/stack/path1",
 *           homeRoute: 0, // it's first push if target is path diffrent.
 *           headerBarParams: () => { ios: { translucent: true } },
 *           routes: [
 *               Route.of({
 *                   path: "/stack/path1",
 *                   build: (match, state, router) => new Page1(state.data, router)
 *               }),
 *               Route.of({
 *                   path: "/stack/path2",
 *                   routeShouldMatch: (route, nextState) => {
 *                       if (!nextState.routeData.applied) {
 *                           // blocks route changing
 *                           return false;
 *                       }
 *                       return false;
 *                   },
 *                   build: (router, route) => {
 *                       const { routeData, view } = route.getState();
 *                       return new Page2(routeData, router);
 *                   }
 *               })
 *           ]
 *       })
 * ...
 *
 * @since 1.0.0
 * @extends {Route}
 */
class Router extends Route {
  static getGlobalRouter() {
    return historyController;
  }
  static getHistoryStack() {
    return history.slice();
  }

  static getLastHistory() {
    return history[history.length - 1];
  }

  static getHistoryByIndex(index) {
    return history[index];
  }
  /**
   * Factory method to create a new Router instance
   *
   * @param {RouterParams} props
   */
  static of(props = {}) {
    return new Router(props);
  }

  static createBlocker(fn) {
    return (router, path, routeData, action, doneFn) => {
      fn(path, routeData, action, ok => ok && doneFn());
    };
  }

  /**
   * @constructor
   * @param {RouterParams} param
   */
  constructor({
    path = "",
    build = null,
    routes = [],
    exact = false,
    sensitive = true,
    strict = true,
    isRoot = false,
    to = null,
    modal = false,
    routerDidEnter,
    routerDidExit,
    routeShouldMatch,
    routeWillEnter = null,
    homeRoute = null
  }) {
    super({ path, modal, build, routes, to, isRoot, routeShouldMatch });
    this._homeRoute = homeRoute;
    this._historyUnlisten = () => null;
    this._handlers = {
      routerDidEnter,
      routerDidExit,
      routeWillEnter
    };

    if (isRoot) {
      store = createStore();
      // this._store = createStore();
      /** @type {HistoryListener} */
      listeners.clear();
      historyController = createHistory({
        sensitive,
        strict,
        exact,
        path,
        getUserConfirmation: (blockerFn, callback) => {
          return blockerFn(callback);
        }
      });

      const pushHomes = path => {
        const matches = matchRoutes(store, [this].concat(this._routes), path);
        let len = 0;
        while (++len < matches.length) {
          let route = matches[len].route;
          if (route.__is_router && route.hasHome()) {
            route.pushHomeBefore && route.pushHomeBefore(path);
          }
        }
      };

      this.initialize(
        historyController,
        (location, action, target, fromRouter = true) =>
          this.onHistoryChange(location, action, target, fromRouter),
        pushHomes
        // pushIndexes.call(this, path)
      );
    }

    this._isRoot = isRoot;
    this._exact = exact;
    this._strict = strict;
    this._sensitive = sensitive;
    this._fromRouter = false;
  }

  /**
   * Router is initialized by parent
   *
   * @protected
   * @since 1.0.0
   * @param {HistoryListener} parentHistory
   * @param {function} onHistoryChange Root onHistoryChange handler
   * @param {function} pushHomes It uses in order to push routers' home-route
   */
  initialize(parentHistory, onHistoryChange, pushHomes) {
    this._pushHomes = pushHomes;
    /** @type {HistoryController} */
    this._historyController = parentHistory.createNode(
      Object.assign({}, this._options, {
        getUserConfirmation: (blockerFn, callback) => {
          return blockerFn(callback);
        }
      })
    );

    this._routes.forEach(route => {
      route.initialize &&
        route.initialize(this._historyController, onHistoryChange, pushHomes);
    });

    this._unlisten = this._historyController.listen((location, action) => {
      onHistoryChange(location, action, this, true); // fires root's onHistoryChange
    });

    // changes route without history
    this.dispatch = (location, action, target, fromRouter = false) => {
      onHistoryChange(location, action, target, fromRouter);
    };
  }

  hasHome() {
    return this._homeRoute !== null;
  }

  /**
   * Fast router's instance checking
   *
   * @since 1.0.0
   * @return {boolean}
   */
  get __is_router() {
    return true;
  }

  /**
   * @ignore
   * @since 1.0.0
   */
  getHistory() {
    return this._historyController.history;
  }

  /**
   * Return current active url
   *
   * @since 1.0.0
   * @return {string}
   */
  getCurrentUrl() {
    return this._currentUrl;
  }

  /**
   * it tests that Whether router can go back as n
   * @example
   * ...
   * router.canGoBack(-3) ? router.goBackto(-3) : // if not, do anything else
   *
   * @since 1.1.0
   * @param {number} n Amount of back as negative value
   * return {boolean}
   */
  canGoBack(n) {
    return this._historyController.canGoBack(n);
  }

  /**
   * Adds eventlisteners to listen history changes
   * @example
   * const unlisten = router.listen((location, action) => {
   *     console.log(`new route location: ${location.url} action : ${action}`);
   * });
   *
   * @since 1.0.0
   * @param {HistoryListener} fn
   */
  listen(fn) {
    listeners.add(fn);

    return () => listeners.delete(fn);
  }

  /**
   * Adds route block handler to history. When history is changed in anywhere
   * then the handler intercepts before history is changed.
   * @example
   * var unload = router.addRouteBlocker((path, routeData, action, ok) => {
   * alert({
   *  message: "Would you like to answer?",
   *  title: "Question", //optional
   *  buttons: [{
   *  text: "Yes",
   *      type: AlertView.Android.ButtonType.POSITIVE,
   *      onClick: function() {
   *        ok(true);
   *      }
   *    },
   *    {
   *      text: "No",
   *      type: AlertView.Android.ButtonType.NEGATIVE,
   *      onClick: function() {
   *        ok(false);
   *      }
   *    }
   *  ]
   *  });
   * });
   *
   * @since 1.0.0
   * @param {RouterBlockHandler} fn
   */
  addRouteBlocker(fn) {
    Router.blocker = Router.createBlocker(fn);
    this._unblock = () => (Router.blocker = null);

    return this._unblock;
  }

  getStore() {
    return store;
  }

  /**
   * Handles history changes. Just Root router,
   * because routing flow mmust be always root to children,
   * in order to change display logic all the time.
   *
   * @since 1.0.0
   * @protected
   * @param {RouteLocation} location
   * @param {string} action
   * @param {Router} target Target Router which pushed to its router.
   * @param {boolean} [fromRouter=true]
   */
  onHistoryChange(location, action, target, fromRouter = true) {
    if (this._isRoot) {
      this._matches = matchRoutes(
        this.getStore(),
        [this].concat(this._routes),
        location.url
      );
      this.renderMatches(this._matches, location, action, target, fromRouter);
    }
  }

  /**
   * Removes last entry from history without trigger history is changed.
   *
   * @since 1.0.0
   * @ignore
   * @protected
   */
  routeRollback() {
    this._historyController.rollback();
  }

  /**
   * Renders route matches by requested path
   *
   * @protected
   * @since 1.0.0
   * @emits RouteShouldMatchHandler
   * @param {Array<{isExact: boolean,params: object,path: string,url: string}>} matches Route matches of the current request
   * @param {RouteLocation} location Current location
   * @param {string} action Current history action
   * @param {Router} target Target Router which pushed to its router.
   * @param {boolean} fromRouter If the specified request if from the router or an another source.
   */
  renderMatches(matches, location, action, target, fromRouter) {
    this._fromRouter = fromRouter;
    const routeData = location.state;

    matches.some(({ match, route }, index) => {
      route.setState({
        hash: location.hash,
        query: location.query,
        rawQuery: location.rawQuery,
        action,
        match
      });
      if (match.isExact !== true && route !== this && route.__is_router) {
        // if(index > 0 && this._isRoot)
        tasks.push((url, action) => {
          this.routeWillEnter &&
            this.routeWillEnter(route, url, action, false, target);
          // handleRouteUrl(this, url, routeData, action);
        }); // add new router display logic from root to children

        route.setUrl(location.url);

        // move routes to child router
        route.renderMatches(
          matches.slice(index, matches.length),
          location,
          action,
          target,
          fromRouter,
          this
        );

        return true;
      } else if (match.isExact === true) {
        const redirection = funcorVal(route.getRedirectto(), [this, route]);
        if (redirection && redirection !== match.url) {
          tasks = []; // reset tasks
          target.routeRollback(); // remove redirected path from target Router
          //  because real path can be owned by different router.
          // -----
          // And then trigger redirection path.
          target.redirectRoute(route, routeData, action, target);
          return false;
        }

        const routingState =
          (route.getRoutingState &&
            route.getRoutingState(route._state, {
              match,
              action,
              routeData
            })) ||
          {};
        // change route state to move data to callbacks
        route.setState({
          query: location.search,
          match,
          action,
          routeData,
          routingState
        });
        route.setUrl(location.url);

        // If route owned by current child router which is different from target router
        // then push or pop route to child router's history.
        // Because current router isn't aware of the route.
        if (target != this) {
          handleRouteUrl(this, match.url, routeData, action);
        }

        // View operations must leave to end of rendering,
        // because there must not render any view before route blocking.
        // An another reason is we build views from child to parent but routing happens
        // parent to child.
        tasks.push(
          (url, action) =>
            this.routeWillEnter &&
            this.routeWillEnter(route, url, action, true, target)
        );

        _lastRoute && _lastRoute.routeDidExit(this);
        this.routeDidMatch(route); // fires routeDidMatch
        if (this._fromRouter && action !== "POP") {
          const view = this.renderRoute(route); // build route's view
          route.setState({ view }); // keep view in the route's state
        }
        tasks.reverse().forEach(task => task(location.url, action)); // trigger all routers' routeWillEnter in the tasks queue
        this.routerDidEnter && this.routerDidEnter(route); // fires routerDidEnter
        route.routeDidEnter(this); // fires routeDidEnter
        _lastRoute = route; // save exact matched route as last route
        this._currentAction = action;
        this._prevRoute = route;
        dispatch(location, action);

        tasks = []; // clear tasks
        return true;
      }
    });

    this._fromRouter = false;

    return true;
  }

  /**
   * Handles a new route which is activated in the router
   *
   * @since 1.0.0
   * @event
   * @protected
   * @param {Route} route
   * @param {string} action
   */
  routeWillEnter(route, action) {
    const viewConroller =
      (route._renderer && route._renderer._rootController) ||
      // else just instance of Route
      route.getState().view;
    this._handlers.routeWillEnter &&
      this._handlers.routeWillEnter(this, route, viewConroller);
  }

  /**
   * @since 1.0.0
   * @ignore
   * @param {string} url
   * @param {string} action
   */
  isUrlCurrent(url, action) {
    const res = this._currentUrl === url && this._currentAction === action;
    return res;
  }

  /**
   * Life-cycle handler emits when Router is activated
   * @example
   * ....
   * Router.of({
   *  routerDidEnter: (router, route) => {
   *    ...
   *  }
   * })
   *
   * @since 1.0.0
   * @emits routerDidEnter
   * @protected
   * @param {Route} route
   */
  routerDidEnter(route) {
    this._handlers.routerDidEnter && this._handlers.routerDidEnter(this, route);
  }

  /**
   * Sets the router statically as active router
   *
   * @since 1.0.0
   * @protected
   * @param {string} action
   */
  setasActiveRouter(action) {
    Router.currentRouter &&
      this != Router.currentRouter &&
      Router.currentRouter.routerDidExit &&
      Router.currentRouter.routerDidExit(action);
    Router.currentRouter = this;
  }

  /**
   * Emits when the router is deactivated.
   *
   * @since 1.0.0
   * @example
   * ....
   * Router.of({
   *  routerDidExit: (router, action) => {
   *    ...
   *  }
   * })
   *
   * ...
   * @emits routerDidExit
   * @param {string} action
   */
  routerDidExit(action) {
    this._handlers.routerDidExit && this._handlers.routerDidExit(this, action);
  }

  /**
   * Redirects route and removes last route's record from the history.
   *
   * @protected
   * @param {Route} route
   * @param {object} routeData
   * @param {string} action
   */
  redirectRoute(route, routeData, action) {
    // redirection of a route
    this.push(funcorVal(route.getRedirectto(), [this, route]), routeData); // and add new route
    // this._historyController.push(route.getRedirectto(), routeData);
  }

  /**
   * @since 1.0.0
   * @private
   * @ignore
   * @param {Page} view
   */
  isViewEmpty(view) {
    return view !== null || view !== undefined;
  }

  /**
   * Route matched to requested url
   *
   * @since 1.0.0
   * @protected
   * @param {Route} route
   */
  routeDidMatch(route) {
    const { match, action, routeData } = route.getState();
    if (match.isExact) {
      const prevUrl = this._currentUrl;
      this.setasActiveRouter(action);
    }
  }

  /**
   * Render route
   *
   * @since 1.0.0
   * @protected
   * @param {Route} route
   * @throws {TypeError}
   */
  renderRoute(route) {
    const view = route.build && route.build(this);
    if (!view) throw new TypeError(`${route} 's View cannot be empty!`);

    return view;
  }

  /**
   * Helper method that pushes the route's url to history
   *
   * @since 1.0.0
   * @protected
   * @param {Route} route
   */
  pushRoute(route) {
    if (!(route instanceof Route))
      throw new TypeError(`route must be instance of Route`);
    this.push(
      funcorVal(route.getRedirectto(), [this, route]) || route.getUrlPath()
    );
  }

  /**
   * Pushes new history entry with specified path
   *
   * @since 1.0.0
   * @param {object|string} path - Path or matches of the route
   * @param {!object} [routeData={}] routeData - Routing data
   * @param {!boolean} [animated={}] routeData - Routing data
   * @return {Router}
   */
  push(path, routeData = {}, animated = true) {
    if (path === this._state.url) {
      Object.assign(this._historyController.history.location.state, {
        routeData
      });
      this.dispatch(
        this._historyController.history.location,
        "PUSH",
        this,
        true
      );

      return this;
    }

    this._fromRouter = true;
    // if (!this.isValidPath(path)) throw new TypeError(`[${path}] Pat h is invalid`);
    if (path.charAt(0) !== "/") {
      path = this._path.getPath() + "/" + path;
    }

    if (Router.blocker) {
      Router.blocker(this, path, routeData, "PUSH", () =>
        this._historyController.push(path, routeData)
      );

      return this;
    }
    try {
      this._pushHomes(path);
    } catch (e) {
      throw e;
    }

    this._historyController.push(path, routeData);
    this._fromRouter = false;
    return this;
  }

  isValidPath(path) {
    return /^(\/\w+)+(\.)?\w+(\?(\w+=[\w\d]+(&\w+=[\w\d]+)*)+){0,1}$/.test(
      path
    );
  }

  /**
   * Replaces specified path's state
   *
   * @experimental
   * @since 1.0.0
   * @ignore
   * @param {string} path
   * @param {data} routeData
   */
  replace(path, routeData) {
    this._historyController.history.replace(path, routeData);
  }

  /**
   * Rewinds the history
   *
   * @since 1.0.0
   * @param {string | RouteLocation} url This is an experimental feature. If you use this feature you should use for same StackRouter stack.
   * @return {Router}
   */
  goBack(url) {
    const go = () => {
      this._fromRouter = true;

      url
        ? this.dispatch(
            typeof url === "string"
              ? { url, hash: "", search: "", state: {} }
              : url,
            "POP",
            this,
            true
          )
        : this._historyController.goBack();
      this._fromRouter = false;
    };
    if (Router.blocker) {
      Router.blocker(this, null, null, "POP", () => go());

      return this;
    }

    go();
  }

  /**
   * Returns last location of history
   *
   * @since 1.0.0
   * @return {RouteLocation}
   */
  getLocation() {
    return this._historyController.history.location;
  }

  /**
   * Returns History entries as Array
   *
   * @since 1.0.0
   * @return {Array<string>}
   */
  getHistoryasArray() {
    return this._historyController.getHistoryasArray();
  }

  /**
   * Forwards history
   *
   * @experimental
   * @ignore
   * @since 1.0.0
   */
  goForward() {
    this._historyController.history.goForward();
  }

  /**
   * Changes route by history index.
   *
   * @experimental
   * @since 1.0.0
   * @ignore
   * @param {number} index
   */
  go(index) {
    this._historyController.go(index);
  }

  /**
   * Adds new route
   *
   * @since 1.0.0
   * @ignore
   * @experimental
   * @param {Route} route
   */
  add(route) {
    this._routes.push(route);
  }

  /**
   * Iterates child routes
   *
   * @since 1.0.0
   * @param {function} fn
   * @return {Array}
   */
  map(fn) {
    return this._routes.map(fn);
  }

  /**
   * Unloads the router
   * @since 1.0.0
   */
  dispose() {
    this._historyUnlisten();
    // if (this._isRoot) {
    this._historyController.dispose();
    this._historyController = null;
    // }
    this._routes.forEach(route => route.dispose());
    this._routes = null;
    this._historyUnlisten = null;
    this._unblock && this._unblock();
    this._unblock = null;
    this._handlers = null;
    this._unlisten && this._unlisten();
    this._listen = null;
  }
}

module.exports = Router;

