    let router2 = Router.of({
      path: "/stack2",
      to: "/stack2/to/1",
      routes: [
        new Route({
          path: "/stack2/to/1",
          build: (router, route) => {
            component2.router = router;
            component2.params = route.getState().match.params;
            return component2;
          }
        }),
        new Route({
          path: "/stack2/to/:id",
          build: (router, route) => {
            component2.url = route.getState().match.url;
            return component2;
          }
        })
      ]
    });

    router2.name = "router2";

    const router = new Router({
      path: "/",
      isRoot: true,
      routes: [router1, router2]
    });

    router.push("/stack1", { name: "name" });
    expect(component1.router).toBe(router1);

    component1.router.push("/stack2", { name: "name" });
    expect(component2.router).toBe(router2);

    component2.router.push("/stack2/to/2", { name: "name" });
    expect(component2.url).toBe("/stack2/to/2");
  });
});

const Route = require("../src/router/Route");
const Router = require("../src/router/Router");
const createStore = require("../src/router/routeStore");
const matchRoutes = require("../src/common/matchRoutes");
const { matchPath, matchUrl } = require("../src/common/matchPath");
const parseUrl = require("../src/common/parseUrl");

describe("Match Routes", () => {
  it("should match same url with query", () => {
    const path = "/path/to/:id";
    {
      const match = matchUrl("/path/to/1", path);
      expect(match).toEqual({
        isExact: true,
        params: { id: "1" },
        path: "/path/to/:id",
        url: "/path/to/1"
      });
    }
    {
      const match = matchUrl("/path/to/1?type=any", path);
      expect(match).toEqual({
        isExact: true,
        params: { id: "1" },
        path: "/path/to/:id",
        url: "/path/to/1"
      });
    }
  });
  it("should parse same url with query", () => {
    const path = "/path/to/:id";
    {
      const res = parseUrl("/path/to/1?type=any&sort=ASC&parent=111");
      expect(res).toEqual({
        hash: "",
        url: "/path/to/1",
        query: { parent: "111", sort: "ASC", type: "any" },
        rawQuery: "?type=any&sort=ASC&parent=111"
      });
      expect(res.query.type).toBe("any");
    }
  });
  it("can match with param", () => {
    const routes = [
      new Route({
        path: "/new-path",
        routes: [new Route({ path: "/new-path/:name" })]
      })
    ];

    const matches = matchRoutes(createStore(), routes, "/new-path/cenkce").map(
      ({ match, route }) => ({ match, route: route.toString() })
    );

    expect(matches).toEqual([
      {
        match: {
          isExact: false,
          params: {},
          path: "/new-path",
          url: "/new-path"
        },
        route: "[object Route, path: /new-path, url: /new-path]"
      },
      {
        match: {
          isExact: true,
          params: { name: "cenkce" },
          path: "/new-path/:name",
          url: "/new-path/cenkce"
        },
        route: "[object Route, path: /new-path/:name, url: /new-path/cenkce]"
      }
    ]);
  });
  it("gets routes if parameter is just numeric", () => {
    const routes = [
      new Route({
        path: "/new-path",
        routes: [new Route({ path: "/new-path/:id(\\d+)" })]
      })
    ];

    {
      const matches = matchRoutes(
        createStore(),
        routes,
        "/new-path/cenkce"
      ).map(({ match, route }) => ({ match, route: route.toString() }));

      expect(matches).toEqual([
        {
          match: {
            isExact: false,
            params: {},
            path: "/new-path",
            url: "/new-path"
          },
          route: "[object Route, path: /new-path, url: /new-path]"
        }
      ]);
    }
    {
      const matches = matchRoutes(createStore(), routes, "/new-path/123").map(
        ({ match, route }) => ({ match, route: route.toString() })
      );
      expect(matches).toEqual([
        {
          match: {
            isExact: false,
            params: {},
            path: "/new-path",
            url: "/new-path"
          },
          route: "[object Route, path: /new-path, url: /new-path]"
        },
        {
          match: {
            isExact: true,
            params: { id: "123" },
            path: "/new-path/:id(\\d+)",
            url: "/new-path/123"
          },
          route: "[object Route, path: /new-path/:id(\\d+), url: /new-path/123]"
        }
      ]);
    }
  });
  it("mathes routes if parameter is alphanumeric", () => {
    const routes = [
      new Router({
        path: "/new-path",
        routes: [new Route({ path: "/new-path/:name([A-Za-z0-9]*)" })]
      })
    ];

    {
      const matches = matchRoutes(
        createStore(),
        routes,
        "/new-path/cenkce"
      ).map(({ match, route }) => ({ match, route: route.toString() }));

      expect(matches).toEqual([
        {
          match: {
            isExact: false,
            params: {},
            path: "/new-path",
            url: "/new-path"
          },
          route: "[object Router, path: /new-path, url: null]"
        },
        {
          match: {
            isExact: true,
            params: { name: "cenkce" },
            path: "/new-path/:name([A-Za-z0-9]*)",
            url: "/new-path/cenkce"
          },
          route:
            "[object Route, path: /new-path/:name([A-Za-z0-9]*), url: /new-path/cenkce]"
        }
      ]);
    }
    {
      const matches = matchRoutes(
        createStore(),
        routes,
        "/new-path/abc123"
      ).map(({ match, route }) => ({ match, route: route.toString() }));
      expect(matches).toEqual([
        {
          match: {
            isExact: false,
            params: {},
            path: "/new-path",
            url: "/new-path"
          },
          route: "[object Router, path: /new-path, url: null]"
        },
        {
          match: {
            isExact: true,
            params: { name: "abc123" },
            path: "/new-path/:name([A-Za-z0-9]*)",
            url: "/new-path/abc123"
          },
          route:
            "[object Route, path: /new-path/:name([A-Za-z0-9]*), url: /new-path/abc123]"
        }
      ]);
    }
  });
});
